public static function getLatest($offset){
		global $CONFIG;	
		$get = "SELECT * FROM `{$CONFIG->dbprefix}ohyes_chat` ORDER by mid DESC";
        $messages = OhYesChat::Data($get, 'get');
		if((int)$offset && !empty($offset)){
		   $latest = array_chunk($messages , $offset);
		   return $latest[0];	
		}
		else {
		 return $messages;	
		}
}
