/**
* Replace icons
*
* @access system
* @return message;
*/
public static function replaceIcon($message){
	$icon = elgg_get_site_url().'mod/OhYesChat/images/emoticons/';
	foreach(OhYesChat::loadIcons() as $codes => $imgs){	
	 	$code[] = $codes;
		$img[] = $imgs;
	}
 	$icons = str_replace($code , $img, $message);
	return $icons;							 
	
}
